  const handlePlaylist = () => {
    console.log("CLICKED")






  }
  return (
    <div className="sidebarOption">
      {Icon && <Icon className="sidebarOption__icon" />}
      {Icon ? <h4>{option}</h4> : <p  onClick={handlePlaylist}>{option}</p>}
    </div>
  );
}

export default SidebarOption;
